import { useState } from "react";

import { TitleHeader } from "./compenents/ui/TitleHeader/TitleHeader";
import { GamePanel } from "./compenents/GamePanel/GamePanel";
import { SidePanel } from "./compenents/SidePanel/SidePanel";
import { LeftContent } from "./compenents/SidePanel/LeftContent";
import { RightContent } from "./compenents/SidePanel/RightContent";
import Main from "./compenents/betting/Main";
import "./assets/styles/gameStyle.scss";
import "reset-css";

const App = () => {
  const [visiblePanel, setVisiblePanel] = useState();
  const handlePanelToggle = (position) => {
    console.log("Clicked position:", position);
    setVisiblePanel((prev) => (prev === position ? null : position));
  };
  return (
    <>
      <TitleHeader />
      <div className="container">
        <SidePanel
          data-position="left"
          content={<LeftContent />}
          style={{
            display: visiblePanel === "left" ? "block" : "none",
          }}
        />
        <GamePanel onPanelToggle={handlePanelToggle} />
        <SidePanel
          data-position="right"
          content={<RightContent />}
          style={{
            display: visiblePanel === "right" ? "block" : "none",
          }}
        />
      </div>
    </>
  );
};

export default App;
