import React , { useEffect , useState } from "react";
import axios  from "axios";
import * as Sound from "../../sound/sound.js";
import * as Function from "../../function.js";

let betlist_complete = [];
let betlist_check = [];
var betlist_prev = [];
let betlimits = [];
let betlimit_min = 0;
let betlimit_max = 0;
let bet_types = "";
// let bet_types = "bothside";
let currency = "KRW"
let betlist = [];
let bet_total = 0;
let win_money = 0;

let end_time_grab = 0;
let begin_time = '';
let end_time = '';
let now_time = '';
let stop_evo_bet = '';
let cutting_cnt = ''
let roundid_now = '';
let round_bet_possible = false;
let langcode = 'ko';
let round_now = '';
let setinterval_status = '';
let bet_status = {
    1 : { 1 : { "amount" : [] , "active" : [] , "picked" : [] , "lock" : [] } } ,
};

let bet_status_base = {
    1 : { 1 : { "amount" : [] , "active" : [] , "picked" : [] , "lock" : [] } } ,
};
let bet_dividend = {
    1 : { "dividend" : 0 } ,
};

function Bet(props) {

    betlimits = props.betlimits;
    betlimit_max = props.betlimit_max;
    betlimit_min = props.betlimit_min;
    console.log(props);
    const [ rebet , setRebet ] = useState(false);
    const [ betCancel , setBetCancel ] = useState(false);
    const [ betComplete , setBetComplete ] = useState(false);
    const [ betCompleteActive , setBetCompleteActive ] = useState(false);
    const [ balance , setBalance ] = useState(0);
    const [ betchip , setBetchip ] = useState(0);
    const [ betchange , setBetchange ] = useState('');
    useEffect(() => {
        fn_bet_init();
        clearInterval(setinterval_status);
        setinterval_status = setInterval(api_status,3000);
    }, [props]);

    const api_status = async () => {

        await axios.post(
            "https://pl.pz.team/minigame/data/status.php",
            new URLSearchParams({
                category: "status",
                gamecode: "rps",
                token: "GEdR9jerB1iReqIkgl%2bxeOzYGfoKymrwLLOe7%2fzMvCbENOcqNa%2brlf3cUKu64Bt1maCx8%2f2Yr5Y%2feU9EpaKnmzHH%2bYwQuxH9",
                gametype: 1,
                group: 1,
            })
        )
        .then(function (response) {
            console.log("3초마다 상태 정보 요청", response.data);
            let _res = response.data;

            fn_status_info(_res);
        })
        .catch(function (error) {
            // 오류발생시 실행
            console.log("실패", error);
        })
        .then(function () {
        });
    }

    Sound.playSound("bgm");
    // 베팅판 클릭 

    const fn_status_info = (_res) => {
        if ( end_time_grab == 0 )
            end_time_grab = _res.stop_bet_before;
        begin_time = Math.floor(+ new Date(_res.begin) / 1000);
        end_time = Math.floor(+ new Date(_res.end) / 1000);
        now_time = Math.floor(+ new Date() / 1000) + end_time_grab;
        

        if ( _res.stop_evo_bet != undefined )
        {
            stop_evo_bet = _res.stop_evo_bet;
            cutting_cnt = _res.cutting_cnt;
    
            if ( stop_evo_bet == true )
            {
                if ( langcode == "ko" )
                    fn_alert(getLang('general','rest_time1') + "<BR>" + getLang('general','rest_time2') +  _res.stop_origin_bet_begin + "~" + _res.stop_origin_bet_end);
                else
                    fn_alert(getLang('general','rest_time1') + "<BR>" +  _res.stop_origin_bet_begin + "~" + _res.stop_origin_bet_end + getLang('general','rest_time2'));
    
                fn_bet_close();
                
                if ( roundid_now == 288 && _res.roundid == 1 )
                {
                    // fn_analysis_today_list();
                    // fn_history_list(1,100,true);
                    fn_api_ajax("balance","");
                    roundid_now = _res.roundid;
                }
                return;
            }

            if ( now_time > end_time )
            {        
                round_bet_possible = false;
                fn_bet_close();
            }
            else
            {
                round_bet_possible = true;
                fn_bet_open();
            }
        }
        else
        {
            if ( now_time > end_time )
            {        
                round_bet_possible = false;
                fn_bet_close();
            }
            else
            {
                round_bet_possible = true;
                fn_bet_open();
            }
        }
    
        if ( roundid_now != _res.roundid && roundid_now != "" )
        {   
            // setTimeout(() => {
                round_bet_possible = true;
                if ( betlist_complete.length > 0 )
                    betlist_prev = betlist_complete;
                betlist_complete = [];
                fn_init();
                fn_bet_open();
                props.setRefresh('refresh' + roundid_now);
                fn_api_ajax("balance","");
                bet_total = 0;
            // }, 5000);
        }
    
        roundid_now = _res.roundid
        round_now = _res.round;

        if ( end_time - now_time > 0 )
        {
            let _time_gab = end_time - now_time;
            let min = parseInt(_time_gab / 60);
            let sec = parseInt(_time_gab - (min * 60));
            console.log(min + ":" + sec);
        }
        else
        {
        }
    };

    const fn_init = () => {
        bet_status = bet_status_base;
    };
    
    const fn_bet_open = () => {
        console.log("fn_bet_open");

        bet_status["1"]["1"]["lock"] = [ false , false , false ];
        bet_status["1"]["1"]["active"] = [ false , false , false ];
        bet_status["1"]["1"]["picked"] = [ false , false , false ];

        for ( var i = 0 ; i < betlist_complete.length ; i++ )
        {
            bet_status[betlist_complete[i].betgroup][betlist_complete[i].betcode]["active"][betlist_complete[i].betvalue-1] = true;
            bet_status[betlist_complete[i].betgroup][betlist_complete[i].betcode]["picked"][betlist_complete[i].betvalue-1] = true;
        }

        if ( betlist.length > 0 )
        {
            for ( var i = 0 ; i < betlist.length ; i++ )
            {
                bet_status[betlist[i].betgroup][betlist[i].betcode]["active"][betlist[i].betvalue-1] = true;
                bet_status[betlist[i].betgroup][betlist[i].betcode]["picked"][betlist[i].betvalue-1] = true;
            }
        }
        setBetComplete(true);
        setBetCancel(true);
        setRebet(true);
    };

    const fn_bet_close = () => {
        console.log("fn_bet_close");
        
        bet_status["1"]["1"]["lock"] = [ true , true , true  ];
        bet_status["1"]["1"]["amount"][0] = [ 0 , 0 , 0 ];


        for ( var i = 0 ; i < betlist_complete.length ; i++ )
        {
            bet_status[betlist_complete[i].betgroup][betlist_complete[i].betcode]["lock"][betlist_complete[i].betvalue-1] = false;
        }

        if ( betlist.length > 0 )
        {
            for ( var i = 0 ; i < betlist.length ; i++ )
            {
                bet_status[betlist[i].betgroup][betlist[i].betcode]["picked"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup][betlist[i].betcode]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup][betlist[i].betcode]["amount"][betlist[i].betvalue-1] = 0;
            }
        }

        setBetComplete(false);
        setBetCancel(false);
        setRebet(false);
    };

    const fn_bet_click = (_group,_code,_value,_rebet) => {
    
        console.log(_group);
        console.log(_code);
        console.log(_value);
    
        if ( round_bet_possible === false )
            return;
    
        Sound.playSound("button_bet",false,0);
    
        let _betcode_min = 0;
        let _betcode_max = 0;
        let _betchip_pick = 0;
        let _exists_code_flag = -1;
        let _bet_money = 0;
        let _bet_money_group = 0;
        let _bet_money_complete = 0;
        let betting_first = 0;
        let betting_last = 0;
        
        for ( var i = 0 ; i < betlist.length ; i++ )
        {
            betting_first += Number(betlist[i].amount);
        } 

        for ( var i = 0 ; i < betlist.length ; i++ )
        {
            if ( bet_types === "bothside" )
            {
                if ( betlist[i].betgroup === _group && betlist[i].betcode === _code && betlist[i].betvalue === _value )
                    _exists_code_flag = i;

                if ( betlist[i].betgroup !== _group )
                    _bet_money += betlist[i].amount;
    
                if ( betlist[i].betgroup === _group && ( betlist[i].betcode !== _code || betlist[i].betvalue !== _value ) )
                    _bet_money_group += betlist[i].amount;
            }
            else
            {
                if ( betlist[i].betgroup === _group )
                    _exists_code_flag = i;
                else
                    _bet_money += betlist[i].amount;
            }
        }

        for ( var i = 0 ; i < betlist_complete.length ; i++ )
        {
            _bet_money_complete += betlist_complete[i].amount;
    
            if ( betlist_complete[i].betgroup === _group && ( betlist_complete[i].betcode !== _code || betlist_complete[i].betvalue !== _value ) )
            {
                _bet_money_group += betlist_complete[i].amount;
            }
        }
    
        for ( var i = 0 ; i < betlist.length ; i++ )
        {
            if ( betlist[i].betgroup === _group && ( betlist[i].betcode === _code && betlist[i].betvalue === _value ) )
                _betchip_pick += betlist[i].amount;
        }

        if ( _rebet === true )
            _betchip_pick = _betchip_pick;
        else
            _betchip_pick += betchip;

        if ( _betchip_pick > ( balance - ( _bet_money + _bet_money_group ) ) )
        {
            _betchip_pick = balance - _bet_money - _bet_money_group;
        }
    
        for ( var i = 0 ; i < betlimits.length ; i++ )
        {
            if ( betlimits[i].betcode === _code )
            {
                _betcode_min = betlimits[i].min;
                _betcode_max = betlimits[i].max;
                break;  
            }
        }

        if ( _betcode_min > _betchip_pick )
        {
            fn_alert(getLang('general','game_111_5') + Function.changeUnit(_betcode_min) + ")");
            return;
        }
    
        if ( _betcode_max < ( _betchip_pick + _bet_money_group ) )
        {
            fn_alert(getLang('general','game_111_6') + Function.changeUnit(_betcode_max) + ")" );
            return;
        }
    
        if ( betlimit_min > (_bet_money + _betchip_pick + _bet_money_group + _bet_money_complete ) )
        {
            fn_alert(getLang('general','game_111_5') + Function.changeUnit(betlimit_min) + ")");
            return;
        }
    
        if ( betlimit_max < (_bet_money + _betchip_pick + _bet_money_group + _bet_money_complete ) )
        {
            fn_alert(getLang('general','game_111_6') + Function.changeUnit(betlimit_max) + ")" );
            return;
        }
    
        if ( _exists_code_flag > -1 )
            betlist.splice(_exists_code_flag, 1);

        betlist.push( { betgroup : _group , betcode : _code , betvalue : _value , amount : _betchip_pick } );

        if ( bet_types !== "bothside" )
        {
            bet_status[_group][_code]["active"] = [];
            bet_status[_group][_code]["amount"] = [];
        }
        bet_status[_group][_code]["amount"][_value-1] = _betchip_pick;
        bet_status[_group][_code]["active"][_value-1] = true;

        if ( betlist.length > 0 )
            setBetCompleteActive(true);
    
        for ( var i = 0 ; i < betlist.length ; i++ )
        {
            betting_last += Number(betlist[i].amount);
        } 
        
        setBalance(balance + betting_first - betting_last);
        setBetchange(_group + "_" + _code + "_" + _value + "_" + _betchip_pick);
    }

    const getLang = (a,b) => {
        return a+b; 
    };
    
    const fn_alert = (a) => {
        alert(a); 
    };
    
    // 버튼에 베팅 배당율 표시
    const fn_bet_init = () => {
        console.log("betlimit");
        // console.log(betlimits);
        // for ( let i = 0 ; i < betlimits.length ; i++ )
        // {
        //     if ( bet_dividend[betlimits[i].betcode] !== undefined )
        //         bet_dividend[betlimits[i].betcode].dividend = betlimits[i].dividend;
        // }
        setBalance(props.balance);
        setBetchip(props.betchip);
    }

    // 베팅 버튼
    const fn_bet_complete = () => {
        // log_check("fn_bet_complete");

        Sound.playSound("button_confirm",false,0);

        setBetComplete(false);
        setTimeout(() => {
            setBetComplete(true);
        }, 500);
        
        if ( round_bet_possible === true )
        {
            if ( betlist.length === 0 )
            {
                fn_alert(getLang('general','nobet'));
                return;
            }
            
            for ( var i = 0 ; i < betlist.length ; i++ )
            {
                bet_status[betlist[i].betgroup][betlist[i].betcode]["active"][betlist[i].betvalue-1] = true;
            }

            let _betlist = JSON.stringify(betlist);
            fn_api_ajax("bet",_betlist);
            setRebet(false);
            setBetCompleteActive(false);
        }
    }

    // 베팅 , 벨런스 요청
    const fn_api_ajax = (category,_data) => {
        
        axios
        .post(
            "https://pl.pz.team/minigame/data/game.php",
            new URLSearchParams({
                category: category,
                token : "k7aTkaJ%2ByeMSfPCVHPxM2jnb49Twx%2BOUTu0ECcsLRLZnRks3rvfLWurUMt1bpsCv6UI9J%2B5mRtFkmIxWibiakd91jG2QCQ5%2BwslfGJyRrKteT3k8eaOlJP%2Fk85RZBfBHlWjOtX9ZbT0cZhZ3NH7jSCUCim2fhMNsicaJ0pnbOvQZ2OYl9rUMYCgUQ3m16nC0WrSfERou99kkcso6a8aFQlpim5o%2FPNs5LHsfwoWc1QM9zS%2BsYk7JqneIMTKeOTe188pKKBiTljkxsTdasFKkAYJVhVHbk8nBfz4Pao2CXRhiFVc9B%2Bi3aDoYfmEKD%2BeCBD3ojStJ7ytURA7E4zS3EhSWnvwnzspPihQG5AkIBR75YAkrob3kHMduxkkze3kIE0smbNVinS%2BIHEXUaIKqhnKBZBpYmwhvrsvdh8C%2BjfKl9iIIPXWC8ey6BcNcH%2B89EBkkMbd5M0lCDRpOIjhYmU3r04QEMSEUvThyLX70%2BsSFQwWu9q9efyHPk92Ful3AzBRdSKkMUERvCRDGbxJYZPxDUzlbIquBdoFiD2FChzSPA%2Flk09GB7%2FigdZiLzpImIpvBj2%2BtYipAeZETtnuZrxTjjEIlw%2F0I1is8YDG7v86AR%2F3C8zLY17pMdue2O%2BZCgKoeZuYwk01X%2BHhvO5rscE5XNuR2O4nCg6TQCuTadxts5Ghc4rZsqJCrBp9G3OFx8WrAkW0Icb5ra%2BNdQYCW1vpHgZL6OsJudQI%3D",
                gamecode: "rps",
                data : _data , 
                gametype: 1,
                group : 1
            })
        )
        .then(function (response) {
            console.log("베팅/잔액", response.data);
            if ( category === "bet" )
                fn_game_bet(response.data.message);
            else    
                setBalance(response.data.message.user_balance.credit);
        })
        .catch(function (error) {
            // 오류발생시 실행
            console.log("실패", error);
        })
        .then(function () {
        });
    }

    // 베팅 api 보낸후 받을때 액션
    const fn_game_bet = (_res) => {
        let list = _res.bet_result;
        setBalance(_res.user_balance.credit);
        for ( var i = 0 ; i < list.length ; i++ )
        {
            if ( list[i].error.code !== 0 )
                fn_alert(getLang("general",list[i].error.code));

            if ( list[i].error.code === 5005 || list[i].error.code === 5012 )
            {
                break;
            }
            else if ( list[i].error.code === 5001 )
            {
                for ( var j = 0 ; j < betlist.length ; j++ )
                {
                    if ( list[i].betcode === betlist[j].betcode && list[i].betvalue === betlist[j].betvalue )
                    {
                        bet_status[betlist[j].betgroup][list[i].betcode]["picked"][list[i].betvalue-1] = false;
                        bet_status[betlist[j].betgroup][list[i].betcode]["active"][list[i].betvalue-1] = false;
                    }
                }
            }
            else
            {
                let _betgroup = 0;
                for ( var j = 0 ; j < betlist.length ; j++ )
                {
                    if ( list[i].betcode === betlist[j].betcode && list[i].betvalue === betlist[j].betvalue )
                    {
                        _betgroup = betlist[j].betgroup;
                        break;
                    }
                }
                betlist_complete.push( {betgroup:Number(_betgroup) , betcode:list[i].betcode , betvalue:list[i].betvalue , amount:list[i].amount} );
            }
        }

        for ( var i = 0 ; i < betlist_complete.length ; i++ )
        {
            let _betgroup = 0;
            for ( var j = 0 ; j < betlist.length ; j++ )
            {
                if ( list[i].betcode === betlist_complete[j].betcode && list[i].betvalue === betlist_complete[j].betvalue )
                {
                    _betgroup = betlist_complete[j].betgroup;
                    bet_status[_betgroup][betlist_complete[i].betcode]["picked"][betlist_complete[i].betvalue-1] = true;
                    bet_status[_betgroup][betlist_complete[i].betcode]["active"][betlist_complete[i].betvalue-1] = true;
                    bet_status[_betgroup][betlist_complete[i].betcode]["lock"][betlist_complete[i].betvalue-1] = false;
                }
            }

            if ( bet_types === "bothside" )
            {
                bet_status[_betgroup][betlist_complete[i].betcode]["lock"][betlist_complete[i].betvalue-1] = true;
            }
            else
            {
                if ( _betgroup === 8 )
                {
                    bet_status[_betgroup]["8"]["lock"][betlist[i].betvalue-1] = true;
                    bet_status[_betgroup]["9"]["lock"][betlist[i].betvalue-1] = true;
                    bet_status[_betgroup]["10"]["lock"][betlist[i].betvalue-1] = true;
                    bet_status[_betgroup]["11"]["lock"][betlist[i].betvalue-1] = true;
                }
                else if ( _betgroup === 12 )
                {
                    bet_status[_betgroup]["12"]["lock"][betlist[i].betvalue-1] = true;
                    bet_status[_betgroup]["15"]["lock"][betlist[i].betvalue-1] = true;
                }
                else
                {
                    bet_status[_betgroup][betlist[i].betcode]["lock"][betlist[i].betvalue-1] = true;
                }
            }
        }

        betlist = [];
        setBetComplete(false);
        fn_bet_calculate();
    }

    // 총 베팅 금액 계산
    const fn_bet_calculate = () => {
        let _bet_money = 0;
        for ( var i = 0 ; i < betlist_complete.length ; i++ )
        {
            _bet_money += betlist_complete[i].amount;
        }
        bet_total = _bet_money;
    }

    // 베팅 취소
    const fn_bet_cancel = () => {

        Sound.playSound("button_cancel",false,0);

        let balance_tmp = 0;
        if ( betlist.length === 0 )
        {
            if ( betlist_complete.length > 0 )
            {
                fn_alert(getLang('general','betCannotBeReset'));
                return;
            }
        }
            
        for ( var i = 0 ; i < betlist.length ; i++ )
        {
            balance_tmp += bet_status[betlist[i].betgroup][betlist[i].betcode]["amount"][betlist[i].betvalue-1];
            bet_status[betlist[i].betgroup][betlist[i].betcode]["active"][betlist[i].betvalue-1] = false;
            bet_status[betlist[i].betgroup][betlist[i].betcode]["picked"][betlist[i].betvalue-1] = false;
            bet_status[betlist[i].betgroup][betlist[i].betcode]["amount"][betlist[i].betvalue-1] = 0;
        }

        setBalance(balance+balance_tmp);
        setBetCompleteActive(false);
        betlist = [];
    }

    // 재베팅
    const fn_rebet = () => {
        
        if ( rebet === false )
            return;
                
        var _exists_code_flag = -1;

        Sound.playSound("button_rebet",false,0);

        if ( betlist_prev.length === 0 )
        {
            fn_alert(getLang("general","notHavePrevBet"));
            return;
        }
        
        for ( var i = 0 ; i < betlist.length ; i++ )
        {
            if ( betlist[i].betgroup === 8 )
            {
                bet_status[betlist[i].betgroup]["8"]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup]["9"]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup]["10"]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup]["11"]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup]["8"]["amount"][betlist[i].betvalue-1] = 0;
                bet_status[betlist[i].betgroup]["9"]["amount"][betlist[i].betvalue-1] = 0;
                bet_status[betlist[i].betgroup]["10"]["amount"][betlist[i].betvalue-1] = 0;
                bet_status[betlist[i].betgroup]["11"]["amount"][betlist[i].betvalue-1] = 0;
            }
            else if ( betlist[i].betgroup === 12 )
            {
                bet_status[betlist[i].betgroup]["12"]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup]["15"]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup]["12"]["amount"][betlist[i].betvalue-1] = 0;
                bet_status[betlist[i].betgroup]["15"]["amount"][betlist[i].betvalue-1] = 0;
            }
            else
            {
                bet_status[betlist[i].betgroup][betlist[i].betcode]["active"][betlist[i].betvalue-1] = false;
                bet_status[betlist[i].betgroup][betlist[i].betcode]["amount"][betlist[i].betvalue-1] = 0;
            }
        }
        betlist = [];
        
        for ( var i = 0 ; i < betlist_prev.length ; i++ )
        {
            _exists_code_flag = 0;

            for ( var j = 0 ; j < betlist_complete.length ; j++ )
            {
                if ( betlist_prev[i].betcode === betlist_complete[j].betcode )
                {
                    _exists_code_flag = 1;
                    break;
                }
            }

            if ( _exists_code_flag === 0 )
            {
                bet_status[betlist_prev[i].betgroup][betlist_prev[i].betcode]["amount"][betlist_prev[i].betvalue-1] = Number(betlist_prev[i].amount);
                fn_bet_click(betlist_prev[i].betgroup,betlist_prev[i].betcode,betlist_prev[i].betvalue,true);
            }
        }

        // fn_bet_calculate();
        // fn_tooltip();
    }

    // 베팅 칩 선택
    const fn_chip_choice = (value) => {
        Sound.playSound("button_slip",false,0);

        setBetchip(Number(value))
    } 

    return (
        <div>
            <div class="pick-grid">
                <div class="pick-board">
                    <p class="tit_pattern" data-lang="general->index_power">파워볼</p>
                    <div class="pick-grid column-4">
                        <div class="pick_button">
                            <button className={"btn under" + 
                                (bet_status["1"]["1"]["active"][0] ? ' active' : '') + 
                                (bet_status["1"]["1"]["picked"][0] ? ' picked' : '') + 
                                (bet_status["1"]["1"]["lock"][0] ? ' lock' : '')}  
                                onClick={ (e) => {fn_bet_click(1,1,1); }} group="5">
                                <p data-lang="general->odd_p">가위</p>
                                <small></small>
                                <div class="bet_monedy">{bet_status["1"]["1"]["amount"][0]}</div>
                            </button>
                        </div>
                        <div class="pick_button">
                            <button className={"btn over" + 
                                (bet_status["1"]["1"]["active"][1] ? ' active' : '') + 
                                (bet_status["1"]["1"]["picked"][1] ? ' picked' : '') + 
                                (bet_status["1"]["1"]["lock"][1] ? ' lock' : '')}  
                                onClick={ (e) => {fn_bet_click(1,1,2); }} group="5">
                                <p data-lang="general->even_p">바위</p>
                                <small></small>
                                <div class="bet_monedy">{bet_status["1"]["1"]["amount"][1]}</div>
                            </button>
                        </div>
                        <div class="pick_button">
                            <button  className={"btn under" + 
                                (bet_status["1"]["1"]["active"][2] ? ' active' : '') + 
                                (bet_status["1"]["1"]["picked"][2] ? ' picked' : '') + 
                                (bet_status["1"]["1"]["lock"][2] ? ' lock' : '')}  
                                onClick={ (e) => {fn_bet_click(1,1,3); }} group="5">
                                <p data-lang="general->odd_p">보</p>
                                <small></small>
                                <div class="bet_monedy">{bet_status["1"]["1"]["amount"][2]}</div>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            <div class="function_bar">
                <div class="f_left">
                    <div className={"unit_btn" + (betCancel ? '' : ' disabled')} onClick={fn_bet_cancel}>
                        <div className="icon">
                            <i className="cancel-betting"></i>
                        </div>
                        <p data-lang="general->betcancel">실행취소</p>
                    </div>
                    <div className={"unit_btn" + (rebet ? '' : ' disabled')} onClick={fn_rebet}>
                        <div className="icon">
                            <i className="re-betting"></i>
                        </div>
                        <p data-lang="general->rebet">재베팅</p>
                    </div>
                </div>
                <div className="f_right">
                    <div className={"unit_btn" + (betComplete ? '' : ' disabled') + 
                        (betCompleteActive ? ' active' : '')} 
                        id="bet_confirm" onClick={fn_bet_complete}>
                        <div className="icon">
                            <i className="bet_confirm"></i>
                        </div>
                        <p data-lang="general->confirm">베팅확인</p>
                    </div>
                    <div className="unit_btn gnb-show" onClick="fn_setting_view()">
                        <div className="icon">
                            <i className="game-setting"></i>
                        </div>
                        <p data-lang="general->setting">더보기</p>
                    </div>
                </div>
            </div>
            <div>
            칩 정보 : 
                {props.betslips.map((array,index) => (
                    <span onClick={ () => {fn_chip_choice(array)}} key={index} value={array}>{array} </span>
                ))}
            <br />
            기본 칩 : 
                {betchip}
            </div>
            <div className="amount_bar">
                <div className="read_only">
                    <div className="info_name" data-lang="general->bet">BET</div>
                    <div className="info_amount" id="info_bet">{bet_total}</div>
                </div>
                                    <div className="read_only">
                    <div className="info_name" data-lang="general->win">WIN</div>
                    <div className="info_amount" id="info_win">{win_money}</div>
                </div>
                                    <div className="read_only">
                    <div className="info_name" data-lang="general->credit">CREDIT</div>
                    <div className="info_amount" id="info_balance">{balance}</div>
                </div>
             </div>
        </div>
    )
}

export default Bet;
