import React, { useEffect, useState } from "react";

let start_flag = 1;
let analysis_list1 = "";
let analysis_list2 = "";
let analysis_list3 = "";
let analysis_list4 = "";
let consecutive_arr = [];
let break_arr = [];
let fondant_arr = [];
export function AnalysisData(props) {
  // list = props.list;
  // console.log(list);
  const [analysisPatternlist1, setanalysisPatternlist1] = useState("");
  const [analysisPatternlist2, setanalysisPatternlist2] = useState("");
  const [analysisPatternlist3, setanalysisPatternlist3] = useState("");
  const [analysisPatternlist4, setanalysisPatternlist4] = useState("");
  const [analysisSize1, setaanalysisSize1] = useState(0);
  const [analysisSize2, setaanalysisSize2] = useState(0);
  const [analysisSize3, setaanalysisSize3] = useState(0);
  const [analysisSize4, setaanalysisSize4] = useState(0);

  const [consecutive, setConsecutive] = useState([]);
  const [breaking, setBreaking] = useState([]);
  const [fondant, setFondant] = useState([]);

  useEffect(() => {
    // if ( start_flag === 1 )
    fn_analysis_grid();
  }, [props]);
  const ptnKind = {
    ODD_EVEN: {
      1: { type: "P", class: "odd", viewText: "odd_s" },
      2: { type: "B", class: "even", viewText: "even_s" },
      0: { type: "T", class: "draw", viewText: "draw_s" },
    },
    UNDER_OVER: {
      1: { type: "P", class: "under", viewText: "under_s" },
      2: { type: "B", class: "over", viewText: "over_s" },
      0: { type: "T", class: "draw", viewText: "draw_s" },
    },
  };

  const viewTextType = "R";
  let list1 = [];
  let list2 = [];
  let list3 = [];
  let list4 = [];
  let mptnList = [];

  const fn_analysis_grid = () => {
    const [list1, setList1] = useState([]);
    const [list2, setList2] = useState([]);
    const [list3, setList3] = useState([]);
    const [list4, setList4] = useState([]);

    const [otherMaxi, setOtherMaxi] = useState([
      [0, 0],
      [0, 0],
      [0, 0],
      [0, 0],
    ]);
    const [otherFall, setOtherFall] = useState([0, 0, 0, 0]);
    const [otherPong, setOtherPong] = useState([0, 0, 0, 0]);

    const [consecutiveArr, setConsecutiveArr] = useState([]);
    const [breakArr, setBreakArr] = useState([]);
    const [fondantArr, setFondantArr] = useState([]);

    useEffect(() => {
      if (props.list) {
        const newList1 = JSON.parse(JSON.stringify(props.list));
        const newList2 = JSON.parse(JSON.stringify(props.list));
        const newList3 = JSON.parse(JSON.stringify(props.list));
        const newList4 = JSON.parse(JSON.stringify(props.list));

        // 각 리스트 변환 로직을 상태 업데이트로 연결
        transformLists(newList1, newList2, newList3, newList4);

        setList1(newList1);
        setList2(newList2);
        setList3(newList3);
        setList4(newList4);
      }
    }, [props.list]);

    const transformLists = (list1, list2, list3, list4) => {
      let prevIndex = [-1, -1, -1, -1];
      let currentMax = [0, 0, 0, 0];
      let currentMark = [0, 0, 0, 0];
      let tempOtherFall = [0, 0, 0, 0];
      let tempOtherPong = [0, 0, 0, 0];
      let tempOtherMaxi = [
        [0, 0],
        [0, 0],
        [0, 0],
        [0, 0],
      ];

      props.list.forEach((item, i) => {
        const {
          sum_odd_even,
          sum_unover,
          powerball_odd_even,
          powerball_unover,
        } = item.result.extrainfo;

        // 리스트별 값 변경
        list1[i].result.value = sum_odd_even === "ODD" ? 1 : 2;
        list2[i].result.value = sum_unover === "UNDER" ? 1 : 2;
        list3[i].result.value = powerball_odd_even === "ODD" ? 1 : 2;
        list4[i].result.value = powerball_unover === "UNDER" ? 1 : 2;

        // 변화된 값들로 다른 계산 수행
        handleListUpdates(
          list1,
          list2,
          list3,
          list4,
          tempOtherFall,
          tempOtherPong,
          tempOtherMaxi,
          prevIndex,
          currentMax,
          currentMark
        );
      });

      // 계산 결과를 상태로 저장
      setOtherFall(tempOtherFall);
      setOtherPong(tempOtherPong);
      setOtherMaxi(tempOtherMaxi);
    };
  };

  function displayKindTab(cnt, opt) {
    if (cnt === 1) {
      mptnList = list1;
      drawLimitElement(
        calcLocation(opt, mptnList, 6, calcSquidResultValue, true, "0"),
        opt,
        "analysis_list1",
        undefined,
        undefined,
        undefined
      );
    } else if (cnt === 2) {
      mptnList = list2;
      drawLimitElement(
        calcLocation(opt, mptnList, 6, calcSquidResultValue, true, "0"),
        opt,
        "analysis_list2",
        undefined,
        undefined,
        undefined
      );
    } else if (cnt === 3) {
      mptnList = list3;
      drawLimitElement(
        calcLocation(opt, mptnList, 6, calcSquidResultValue, true, "0"),
        opt,
        "analysis_list3",
        undefined,
        undefined,
        undefined
      );
    } else if (cnt === 4) {
      mptnList = list4;
      drawLimitElement(
        calcLocation(opt, mptnList, 6, calcSquidResultValue, true, "0"),
        opt,
        "analysis_list4",
        undefined,
        undefined,
        undefined
      );
    }
  }

  const calcSquidResultValue = function (opt, resValue, tie, tieValue) {
    if (resValue.result.extraValue != undefined)
      return resValue.result.extraValue;
    else return resValue.result.value;
  };

  const calcLocation = (
    opt,
    dataList,
    maxLocValue,
    fn_calc,
    tieBool,
    tieValue
  ) => {
    let locArray = new Array();
    let thisValue = null;
    let preValue = null;
    let xloc = 0;
    let yloc = 0;
    let rxloc = 0;
    let prev_tie = false;
    let tie_count = 0;

    dataList.forEach((element, idx) => {
      thisValue = fn_calc(opt, element, tieBool, tieValue);

      if (tieBool && thisValue === null) {
        thisValue = tieValue;
      }

      if (tieBool || thisValue != null) {
        if (idx == 0) {
          locArray[0] = new Array();
          if (element.round.indexOf("value-") > -1) {
            locArray[0][0] = [thisValue, element.round, 0];
          } else {
            locArray[0][0] = [thisValue, element.round.split("-")[1], 0];
          }
          preValue = thisValue;
        } else {
          if (
            preValue == thisValue ||
            (tieBool && thisValue == tieValue) ||
            preValue == tieValue
          ) {
            if (
              yloc + 1 < maxLocValue &&
              (locArray[rxloc][yloc + 1] == null ||
                locArray[rxloc][yloc + 1] == "undefined")
            ) {
              if (tieBool && thisValue == tieValue) {
              } else if (
                locArray[rxloc][yloc + 2] == undefined ||
                (locArray[rxloc][yloc + 2][0] != thisValue &&
                  locArray[rxloc][yloc + 2][0] != tieValue)
              ) {
                if (
                  (locArray[rxloc - 1] &&
                    locArray[rxloc - 1][yloc + 1] &&
                    locArray[rxloc - 1][yloc + 1][0] == thisValue) ||
                  (locArray[rxloc - 1] &&
                    locArray[rxloc - 1][yloc + 1] &&
                    thisValue == tieValue) ||
                  (locArray[rxloc - 1] &&
                    locArray[rxloc - 1][yloc + 1] &&
                    locArray[rxloc - 1][yloc + 1][0] == tieValue &&
                    rxloc > 1)
                ) {
                  rxloc++;
                } else {
                  yloc++;
                }
              } else {
                rxloc++;
              }
            } else {
              rxloc++;
            }

            if (tieBool && thisValue == tieValue) {
              prev_tie = true;
              tie_count += 1;
            } else {
              prev_tie = false;
              tie_count = 0;
            }
          } else if (preValue != thisValue) {
            prev_tie = false;
            tie_count = 0;
            xloc++;
            yloc = 0;
            rxloc = xloc;
          }

          if (!tieBool && thisValue != tieValue) {
            while (
              (locArray[rxloc] && locArray[rxloc][yloc]) ||
              (locArray[rxloc] &&
                locArray[rxloc][yloc + 1] &&
                locArray[rxloc][yloc + 1][0] == tieValue)
            ) {
              rxloc++;
            }
          }

          if (!locArray[rxloc]) {
            locArray[rxloc] = new Array();
          }

          if (element.round.indexOf("value-") > -1) {
            if (tieBool && thisValue == tieValue)
              locArray[rxloc][yloc][2] = tie_count;
            else locArray[rxloc][yloc] = [thisValue, element.round, tie_count];
          } else {
            if (tieBool && thisValue == tieValue)
              locArray[rxloc][yloc][2] = tie_count;
            else
              locArray[rxloc][yloc] = [
                thisValue,
