import { useState } from "react";
import { InputRadio, InputSwitch } from "../Btn/Btn";
import "./MenuList.scss";
export const MenuList = () => {
  const [selectedValue, setSelectedValue] = useState("");
  const [isOn, setIsOn] = useState(false);

  const handleToggle = (e) => {
    setIsOn(e.target.checked);
  };
  const handleChange = (e) => {
    setSelectedValue(e.target.value);
  };

  return (
    <div className="drop_set_box">
      <div className="inner">
        <ul>
          <li>
            <a className="ic-manual">매뉴얼</a>
          </li>
          <li>
            <a className="ic-stats">통계</a>
          </li>
        </ul>
        <ul>
          <li>
            <p className="ic-bgm">BGM</p>
            <InputSwitch checked={isOn} onChange={handleToggle} />
          </li>
          <li>
            <p className="ic-effect">효과음</p>
            <InputSwitch checked={isOn} onChange={handleToggle} />
          </li>
        </ul>
        <ul>
          <li>
            <p className="ic-bet1" href="#!">
              베팅 선택
            </p>
            <InputRadio
              name="betting_board"
              value="option1"
              checked={selectedValue === "option1"}
              onChange={handleChange}
            />
          </li>
          <li>
            <p class="ic-bet2" href="#!">
              베팅 입력
            </p>
            <InputRadio
              name="betting_board"
              value="option2"
              checked={selectedValue === "option2"}
              onChange={handleChange}
            />
          </li>
        </ul>
      </div>
    </div>
  );
};
